

eurusdCsvPath = 'EURUSD_15m_BID_sample.csv';

eurusd = readtable(eurusdCsvPath, 'Format', '%s%f%f%f%f%f');
eurusdOpen = eurusd.('Open');                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
eurusdOpen = eurusdOpen(:);

%% Split into train and test set
split = floor(numel(eurusdOpen)*0.8);    
yTrain = eurusdOpen(1:split);
yTest = eurusdOpen(split+1:end);

%% Build arima model
mdl = arima(5, 0, 6);

%% Estimate parameters
estMdl = estimate(mdl, yTrain);

%% Inference
residual = infer(estMdl, yTest);
y = yTest + residual;

%% Plot predicted values
plot(1:size(yTest, 1), yTest, 'b--', 1:size(y, 1), y, 'r--');

%% Forecast
lags = 6;
numPeriods = 5;
yForecast = zeros(1, size(yTest, 1));
i = 1;
yTest = yTest.';
while i <= (size(yTest, 2) - lags)
    yObs = yTest(i:(i + lags));
    [tmp, ~] = forecast(estMdl, numPeriods, 'Y0', yObs.');
    %% TODO: refactor
    for ii = i:(i+numPeriods-1)
       yForecast(ii) = tmp(ii - i + 1);
    end
    i = i + numPeriods;
end
yTest = yTest.';
%% Plot predicted values
plot(1:size(yTest, 1), yTest, 'b--', 1:size(yTest, 1), yForecast, 'r--');

