%% Build sample model with open price (single-variate timeseries)
%% 2-layered lstm
%%env = 'dumm';
env = 'prod';


%% Load data

if strcmp(env, 'limbo')
    train_csv_path = 'EURUSD_15m_BID_sample.csv';
else
    train_csv_path = 'EURUSD_15m_BID_01.01.2010-31.12.2016.csv';
end
    
dataframe = readtable(train_csv_path, 'Format', '%s%f%f%f%f%f');
open_timeseries = dataframe.('Open');                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
open_timeseries = open_timeseries(:);

%% Split into train and test set
split = floor(numel(open_timeseries)*0.8);

train_data = open_timeseries(1:split+1);    
mu = mean(train_data);
sig = std(train_data);
normalized_train_data = (train_data - mu) / sig;

X_train = train_data(1:end-1).';
y_train = train_data(2:end).';

y_test = open_timeseries(split+1:end).';

%% Build model for Open price
num_features = 1;
num_responses = 1;
num_hidden_units = 200;
lstm_net = [ ...
    sequenceInputLayer(num_features),...
    lstmLayer(num_hidden_units, 'OutputMode', 'sequence'),...
    lstmLayer(num_hidden_units, 'OutputMode', 'sequence'),...
    fullyConnectedLayer(num_responses),...
    regressionLayer
    ];

%% Train the model
training_options = trainingOptions(...
    'adam', ...
    'MaxEpochs', 250, ...
    'GradientThreshold',1, ...
    'InitialLearnRate',0.005, ...
    'LearnRateSchedule','piecewise', ...
    'LearnRateDropPeriod',125, ...
    'LearnRateDropFactor',0.2, ...
    'Verbose',0, ...
    'Plots','training-progress', ...
    'ExecutionEnvironment','multi-gpu'...
    );
lstm_net = trainNetwork(X_train,y_train,lstm_net,training_options);

%% Save the model
model_filename = strcat('lstm_', datestr(now,'yyyymmddTHHMMSS'));
model_filename = strcat(model_filename, '.mat');
save(model_filename, 'lstm_net');

y_pred = [];
numTimeStepsTest = numel(y_test);
for i = 1:numTimeStepsTest
    [lstm_net, y_pred(:,i)] = predictAndUpdateState(lstm_net,y_test(:,i),'ExecutionEnvironment','cpu');
end
y_pred = sig*y_pred + mu;

rmse = sqrt(mean((y_pred-y_test).^2));

figure
subplot(2,1,1)
plot(y_test)
hold on
plot(y_pred,'.-')
hold off
legend(["Observed" "Predicted"])
ylabel("Cases")
title("Forecast with Updates")

subplot(2,1,2)
stem(y_pred - y_test)
xlabel("Month")
ylabel("Error")
title("RMSE = " + rmse)
